import React from 'react';
import './product-item.styles.scss';

import CustomButton from '../../components/custom-button/CustomButton';
import { addItem } from '../../redux/actions/cart';
import { connect } from 'react-redux';

import { selectProduct } from '../../redux/selectors/shop.selector';

const ProductItem = ({ item, addItem }) => {
  console.log(item);
  const { name, imageUrl, price } = item;
  return (
    <div className='product-container'>
      <div className='main-product-container'>
        <div className='main-image-container'>
          <div className='large-image'>
            <img src={imageUrl} alt='product-image' className />
          </div>
          <div className='mini-images'></div>
        </div>

        <div className='description-container'>
          <h4 className='product-title'>{name}</h4>
          <h5 className='price'>${price}</h5>
          <p> This is a piece of clothing that you can wear</p>
          <p className='product-color-chart'></p>

          <div class='product-size-dropdown'>
            <button class='dropbtn'>
              Sizes
              <i class='fa fa-caret-down'></i>
            </button>
            <div class='dropdown-content'>
              <a href='#'>Link 1</a>
              <a href='#'>Link 2</a>
              <a href='#'>Link 3</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state, ownProps) => ({
  item: selectProduct(
    ownProps.match.params.productId,
    ownProps.match.params.collectionId
  )(state),
});
export default connect(mapStateToProps, { addItem })(ProductItem);
// export default ProductItem;
