import React from 'react';
import PropTypes from 'prop-types';
import IconButton from '@material-ui/core/IconButton';
import { ReactComponent as ShoppingIcon } from '../../assets/shopping-bag.svg';
import { connect } from 'react-redux';
import { toggleCart } from '../../redux/actions/cart';
import { sumQuantity } from '../../utils/cart.utils';

import './cart-icon.styles.scss';

const CartIcon = ({ toggleCart, cart }) => {
  const { cartItems } = cart;
  return (
    <div className='cart-icon'>
      <IconButton onClick={toggleCart}>
        <ShoppingIcon className='shopping-icon' />
        <span className='item-count'>{sumQuantity(cartItems)}</span>
      </IconButton>
    </div>
  );
};

CartIcon.propTypes = {
  toggleCart: PropTypes.func.isRequired,
  cart: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  cart: state.cart,
});

export default connect(mapStateToProps, { toggleCart })(CartIcon);
