import React, { useState } from 'react';
// import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import './header.styles.scss';
import logo from '../../assets/refine-logo.png';
import { connect } from 'react-redux';
import CartIcon from '../cart/CartIcon';
import CartDropdown from '../cart/CartDropdown';
import EyewearDropdownMenu from './Eyewear.dropdown.menu';
import ContactLenseDropdownMenu from './ContactLense.dropdown.menu';

import { createStructuredSelector } from 'reselect';

import { selectCartHidden } from '../../redux/selectors/cart.selector';
import { selectCurrentUser } from '../../redux/selectors/user.selector';
import {
  selectDropdown,
  selectProduct,
} from '../../redux/selectors/directory.selector';

import { signOutStart } from '../../redux/actions/user';
import {
  toggleDropdown,
  toggleEyewear,
  toggleSunwear,
  toggleContacts,
} from '../../redux/actions/directory';

const Header = ({
  currentUser,
  hidden,
  signOutStart,
  productSelection,
  dropdownMenu,
  toggleDropdown,
}) => {
  const [catalog, setCatalog] = useState({
    productType: '',
    isEyewear: false,
    isSunwear: false,
    isContact: false,
  });

  const { isEyewear, isSunwear, isContact } = productSelection;
  // const renderDropdown = () => {
  //   if (catalog.isEyewear) {
  //     setCatalog({ ...catalog, isEyewear: !catalog.isEyewear });
  //     // return <EyewearDropdownMenu productType={catalog.productType} />;
  //   } else if (catalog.isSunwear) {
  //     setCatalog({ ...catalog, isSunwear: !catalog.isSunwear });
  //     // return <EyewearDropdownMenu productType={catalog.productType} />;
  //   } else if (catalog.isContact) {
  //     setCatalog({ ...catalog, isContact: !catalog.isContact });
  //     // return <ContactLenseDropdownMenu productType={catalog.productType} />;
  //   } else {
  //     return;
  //   }
  // };

  const handleDropdownClick = (productType) => {
    console.log('fired');

    if (productType == 'eyeglasses') {
      setCatalog({
        ...catalog,
        productType,
        isEyewear: !catalog.isEyewear,
        isSunwear: false,
        isContact: false,
      });
    } else if (productType == 'sunglasses') {
      setCatalog({
        ...catalog,
        productType,
        isEyewear: false,
        isSunwear: !catalog.isSunwear,
        isContact: false,
      });
    } else {
      setCatalog({
        ...catalog,
        productType,
        isEyewear: false,
        isSunwear: false,
        isContact: !catalog.isContact,
      });
    }
  };

  return (
    <div className='header'>
      <div className='row'>
        <div className='options-left'>
          <Link className='option' to='/men'>
            MEN
          </Link>
          <Link className='option' to='/women'>
            WOMEN
          </Link>
        </div>

        <div className='options-center'>
          <Link className='option' to='/'>
            <img src={logo} alt='logo' />
          </Link>
        </div>

        <div className='options-right'>
          <Link className='option' to='/shop'>
            SHOP
          </Link>

          {currentUser ? (
            <div className='option' onClick={() => signOutStart()}>
              SIGN OUT
            </div>
          ) : (
            <Link className='option' to='/login'>
              LOGIN
            </Link>
          )}

          <Link className='option' to='/cart'>
            CONTACT
          </Link>

          <CartIcon />
        </div>
        {hidden ? null : <CartDropdown />}
      </div>

      <div className='row'>
        <div className='nav-links'>
          <div className='nav-links__wrapper'>
            <a onClick={() => handleDropdownClick('eyeglasses')}>Eyeglasses</a>
          </div>
          <div className='nav-links__wrapper'>
            <a onClick={() => handleDropdownClick('sunglasses')}>Sunglasses</a>
          </div>

          <div className='nav-links__wrapper'>
            <a onClick={() => handleDropdownClick('contacts')}>
              Contact Lenses
            </a>
          </div>
        </div>
      </div>
      {catalog.isEyewear || catalog.isSunwear ? (
        <EyewearDropdownMenu productType={catalog.productType} />
      ) : catalog.isContact ? (
        <ContactLenseDropdownMenu productType={catalog.productType} />
      ) : null}
    </div>
  );
};

// {
//   catalog.isEyewear || catalog.isSunwear ? (
//   <EyewearDropdownMenu productType={catalog.productType} />
// ) : catalog.isContact ? (
//   <ContactLenseDropdownMenu productType={catalog.productType} />
// ) : null
// }

const mapStateToProps = createStructuredSelector({
  hidden: selectCartHidden,
  currentUser: selectCurrentUser,
  dropdownMenu: selectDropdown,
  productSelection: selectProduct,
});

const mapDispatchToProps = (dispatch) => ({
  signOutStart: () => dispatch(signOutStart()),
  toggleDropdown: () => dispatch(toggleDropdown()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Header);
