import React from 'react';
import './product-styles.scss';
import { connect } from 'react-redux';
import { selectorProduct } from '../../redux/selectors/shop.selector';

const ProductItem = ({ collection }) => {
  console.log(match);
  return (
    <div className='product-container'>
      <div className='description-column'>DESCRIPTION</div>
      <div className='product-images'>SCROLLABLE IMG CONTENT</div>
      <div className='add-to-column'>PRICE, Add to bag</div>
    </div>
  );
};

const mapStateToProps = (state, ownProps) => ({
  collection: selectorProduct(ownProps.match.params.productId)(
    ownProps.match.params.collectionId
  )(state),
});
export default connect(mapStateToProps)(ProductItem);
