import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  USER_LOADED,
  AUTH_ERR,
  LOGOUT,
  SET_USER,
  SIGNIN_SUCCESS,
  SIGNIN_FAILURE,
} from '../actions/types';

const initialState = {
  currentUser: null,
  error: null,
};

export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case SIGNIN_SUCCESS:
      return {
        ...state,
        currentUser: payload,
        error: null,
      };
    case SIGNIN_FAILURE:
      return {
        ...state,
        error: payload,
      };
    case SET_USER:
      return {
        ...state,
        loading: false,
        currentUser: payload,
      };
    case REGISTER_SUCCESS:
    case LOGIN_SUCCESS:
      return {
        ...state,
        loading: false,
        currentUser: payload,
      };
    default:
      return state;
  }
}
