import React, { useReducer } from 'react';

const INITIAL_STATE = {
  x: 0,
  trending: [{}],
};

const reducer = (state, action) => {
  switch (action.type) {
    case SLIDE_LEFT:
      return {
        ...state,
        x: state.x === 0 ? -100 * (state.trending.length - 1) : state.x + 100,
      };
    case SLIDE_RIGHT:
      return {
        ...state,
        x: state.x === -100 * (state.trending.length - 1) ? 0 : state.x - 100,
